import React from "react";
import axios from "axios";
import {Text, View, Image, TouchableOpacity, StyleSheet} from "react-native";

export default class Answer extends React.Component {
    render(){
        const {answer} = this.props;
        console.log(answer);
        return(
            <View style={styles.shadow}>
            <View style={styles.container}>
                {/* <Image style={styles.img} source={{uri: product.images[0].url}}/> */}
                <View style={styles.info}>
                    <Text style={styles.name}>CÃ¢u {answer.name}</Text>
                    <View style={styles.priceRow}>
                        <Text style={styles.price}>{answer.A}</Text>
                        <Text style={styles.price}>{answer.B}</Text>
                        <Text style={styles.price}>{answer.C}</Text>
                        <Text style={styles.price}>{answer.D}</Text>
                        <TouchableOpacity onPress={onAddToCartClick}>
                            <Text style={styles.cartText}>Mua +</Text>
                        </TouchableOpacity>
                    </View>
                </View>
            </View>
        </View>
        );
    }
}

const styles = StyleSheet.create({
    cartText: {
            textTransform: 'uppercase',
            fontSize: 16,
            color: '#2f95dc'
        },
        shadow: {
            shadowColor: '#000',
            shadowOpacity: 0.1,
            shadowRadius: 10,
            shadowOffset: {width: 0, height: 0}
        },
        container: {
            marginBottom: 20,
            borderRadius: 4,
            backgroundColor: '#fff',
            overflow: 'hidden'
        },
        info: {
            padding: 8
        },
        img: {
            height: 150,
            borderTopLeftRadius: 4,
            borderBottomLeftRadius: 4
        },
        name: {
            fontSize: 16,
            marginBottom: 8
        },
        priceRow: {
            flexDirection: 'row',
            alignItems: 'center'
        },
        price: {
            fontSize: 16,
            color: '#888',
            flex: 1
        }
})
