import React from 'react';
import axios from 'axios';
import { StatusBar } from 'expo-status-bar';
import { StyleSheet, Text, View, ScrollView, FlatList } from 'react-native';
import QuestionsListItem from '../components/QuestionsListItem';
import Answer from './Answer';

export default class Category extends React.Component {
  constructor(props){
    super(props);
    this.state= {
      products:[]
    }
  }
  
  componentDidMount(){
    // const id = this.props.route.params.categoryId;
    // const a = '/products?category='+id;
    // console.log(a);
    const id = this.props.route.params.id_categories;
    axios.get('/questions?id_categories='+id)
      .then(res =>{
        this.setState({
          questions: res.data
        })
      })
      .catch(error=>{
        console.error(error)
      })
  }
  render(){
    const {questions} = this.state;
    return (
      <FlatList 
        data={questions}
        contentContainerStyle={styles.container}
        numColumns={5}//số cột hiển thị mong muốn
        renderItem={({item})=> 
          <View style={styles.wrapper}>
            <QuestionsListItem 
              questions={item}
              onPress={()=>navigation.navigate('Answer', {
                id_questions: item.id,
                questionsName: item.name,
                A: item.A,
                B: item.B,
                C: item.C,
                D: item.D,
              })}  
            />
          </View>
        }
        keyExtractor={(item) => item.id}
      />
    );
  }
}

const styles = StyleSheet.create({
  container: {
    paddingHorizontal: 8,
    paddingTop: 16
  },
  wrapper: {
    flex: 1,
    paddingHorizontal: 8
  }
});
