import React from 'react';
import { StatusBar } from 'expo-status-bar';
import { StyleSheet, Text, View, ScrollView, FlatList } from 'react-native';
import ProductListItem from '../components/ProductListItem';

export default class Category extends React.Component {
  constructor(props){
    super(props);
    this.state= {
      products:[
        {
          id: 1,
          images: [
            {
              url: 'https://1.bp.blogspot.com/-TuCsF01fE2M/XroLo8YnPeI/AAAAAAAAlhk/szLN8QjJ918lHUB848AdNqfe36VuN0gtQCLcBGAsYHQ/s1600/hinh-nen-de-thuong-mau-hong-pink-anime-girl.png'
            }
          ],
          name: 'Sản phẩm 1',
          price: '50000'
        },
        {
          id: 2,
          images: [
            {
              url: 'https://1.bp.blogspot.com/-TuCsF01fE2M/XroLo8YnPeI/AAAAAAAAlhk/szLN8QjJ918lHUB848AdNqfe36VuN0gtQCLcBGAsYHQ/s1600/hinh-nen-de-thuong-mau-hong-pink-anime-girl.png'
            }
          ],
          name: 'Sản phẩm 2',
          price: '100000'
        }
      ]
    }
  }
  render(){
    return (
      <FlatList 
        data={this.state.products}
        contentContainerStyle={styles.container}
        numColumns={2}//số cột hiển thị mong muốn
        renderItem={({item})=> 
          <View style={styles.wrapper}>
            <ProductListItem product={item}/>
          </View>
        }
        keyExtractor={(item) => item.id}
      />
    );
  }
}

const styles = StyleSheet.create({
  container: {
    paddingHorizontal: 8,
    paddingTop: 16
  },
  wrapper: {
    flex: 1,
    paddingHorizontal: 8
  }
});
