import React from "react";
import axios from "axios";
import {Text, View, Image, TouchableOpacity, StyleSheet, Alert} from "react-native";

import Img from '../assets/5291450.jpg';

export default class Answer extends React.Component {
    check(answer){
        if(answer==this.props.route.params.answer){
            Alert.alert('Ok');
            console.log(styles.true);
            styles.true={
                position: 'absolute',
                display: 'flex'
            }
            console.log(styles.true);
        }
        else{
            Alert.alert('No');
        }
    }
    render(){
        const answer = this.props.route.params;
        console.log(answer);
        return(
            <View style={styles.shadow}>
                <View style={styles.container}>
                    <Image style={styles.img} source={Img}/>
                    <View style={styles.info}>
                        <Text style={styles.name}>{answer.questionName}</Text>
                        <View style={styles.answers}>
                            <View style={styles.answer}>
                                <TouchableOpacity style={styles.choice} onPress={()=>this.check(answer.a)}>
                                    <Text style={styles.txt}>A. {answer.a}</Text>
                                </TouchableOpacity>
                                <TouchableOpacity style={styles.choice} onPress={()=>this.check(answer.b)}>
                                    <Text style={styles.txt}>B. {answer.b}</Text>
                                </TouchableOpacity>
                            </View>
                            <View style={styles.answer}>
                                <TouchableOpacity style={styles.choice} onPress={()=>this.check(answer.c)}>
                                    <Text style={styles.txt}>C. {answer.c}</Text>
                                </TouchableOpacity>
                                <TouchableOpacity style={styles.choice} onPress={()=>this.check(answer.d)}>
                                    <Text style={styles.txt}>D. {answer.d}</Text>
                                </TouchableOpacity>
                            </View>
                        </View>
                    </View>
                </View>

                <View style={styles.true}>
                    <Text>OK</Text>
                </View>

                
            </View>
        );
    }
}

const styles = StyleSheet.create({
    shadow: {
        flex: 1,
        backgroundColor: '#fff',
        shadowColor: '#000',
        shadowOpacity: 0.1,
        shadowRadius: 10,
        shadowOffset: {width: 0, height: 0},
    },
    container: {
        marginBottom: 20,
        borderRadius: 4,
        backgroundColor: '#fff',
        overflow: 'hidden'
    },
    info: {
        padding: 8,
    },
    name: {
        fontSize: 16,
        marginBottom: 8
    },
    answers: {
        backgroundColor: '#90cbd3',
    },
    choice: {
        backgroundColor: '#fff',
        alignItems: 'center',
        margin: 10,
        paddingHorizontal: 30,
        paddingVertical: 10,
        borderRadius: 10,
    },
    answer: {
        flexDirection: 'row',
        justifyContent: 'space-between',
    },
    txt: {
        fontSize: 24,
        fontFamily: 'UVNBanhMi',
        fontWeight: 'bold',
        color: '#90cbd3',
    },
    img: {
        height: 420,

    },
    true: {
        position: 'absolute',
        display: 'none'
    }
})
