import React from "react";
import { Alert, StyleSheet, Text, TouchableOpacity, View } from 'react-native';


export default class Notification extends React.Component {

    constructor (props){
        super(props);
        this.state={
            _notification: "",
            check: false,
        }
    }

    componentDidMount(){
        const notification =  this.props.route.params.notification;
        if(notification){
            this.setState({_notification: "Hoan hô đúng rồi", check: true})
        }
        else{
            this.setState({_notification: "Rất tiếc trả lời sai rồi", check: false})
        }
    }

    render(){
        
        return (
            <View style={styles.container}>
                <View style={styles.body}>
                    <Text style={styles.not}>{this.state._notification}</Text>
                    {()=>{
                        if(this.state.check){
                            return(
                                <View style={styles.btns}>
                                    <TouchableOpacity>
                                        <Text style={styles.btn}>Giải thích</Text>
                                    </TouchableOpacity>
                                    <TouchableOpacity>
                                        <Text style={styles.btn}>Câu tiếp</Text>
                                    </TouchableOpacity>
                                </View>
                            )
                        }
                        else{
                            <View style={styles.btns}>
                                <TouchableOpacity>
                                    <Text style={styles.btn}>Làm lại</Text>
                                </TouchableOpacity>
                                <TouchableOpacity>
                                    <Text style={styles.btn}>Giải thích</Text>
                                </TouchableOpacity>
                            </View>
                        }
                    }}
                </View>
            </View>
        );
    }
}

const styles= StyleSheet.create({
    container: {
        flex: 1,
        backgroundColor: "#fff",
    },
    body: {
        backgroundColor: "red",
        flex: 1,
        width: "100%",
        alignItems: "center",
        justifyContent: "center",
    },
    not: {
        fontFamily: "UVNBanhMi",
        fontSize: 50,
        fontWeight: "bold",
        textAlign: "center",
        color: "#fff",
    },
    btns: {
        display: "flex",
        flexDirection: "row",
        width: "100%",
        padding: 20,
        justifyContent: "space-between",
    },
    btn: {
        backgroundColor: "#000",
        fontFamily: "UVNBanhMi",
        color: "#fff",
        paddingHorizontal: 30,
        paddingVertical: 10,
    }
});
