import React from 'react';
import axios from 'axios';
import { StyleSheet, Text} from 'react-native';
import {NavigationContainer} from '@react-navigation/native';
import Categories from "./screen/Categories";
import Category from "./screen/Category";
import { createStackNavigator } from '@react-navigation/stack';
import {createBottomTabNavigator} from '@react-navigation/bottom-tabs';
import { StatusBar } from 'expo-status-bar';
import Shop from './screen/Shop';
import Setting from './screen/Setting';
import Answer from './screen/Answer';
import Game from './screen/Game';
import MaterialCommunityIcons from 'react-native-vector-icons/MaterialCommunityIcons';
import * as Font from 'expo-font';
import BgAudio from './react-native-background-audio';

axios.defaults.baseURL='http://6335-113-175-243-56.ngrok.io';

const Stack = createStackNavigator();
const Tab = createBottomTabNavigator();

export class Home extends React.Component{

  state = {
    fontsLoaded: false,
  };

  async loadFonts() {
    await Font.loadAsync({
      'Vanilla': require('./assets/fonts/Vanilla.ttf'),
      'AntsyPants': require('./assets/fonts/AntsyPants.ttf'),
      'UVNBanhMi': require('./assets/fonts/UVNBanhMi.ttf'),
    });
    this.setState({ fontsLoaded: true });
  }

  async componentDidMount() {
    this.loadFonts();
  }

  render(){
    return (
        <Stack.Navigator
          screenOptions={{
            headerStyle: {
              backgroundColor: '#90cbd3',
            },
            headerTintColor: '#fff',
            headerTitleStyle: {
              fontWeight: '900',
              fontSize: 30,
              fontFamily: 'UVNBanhMi',
            }
          }}
        >
          <Stack.Screen 
            name="Categories" 
            component={Categories} 
            options={{
              title: 'Trang chủ'
            }}
          />
          <Stack.Screen 
            name="Category" 
            component={Category} 
            options={({ route }) => ({
            title: route.params.categoryName
            })}
          />
          <Stack.Screen 
            name="Answer" 
            component={Answer} 
            options={({ route }) => ({
            title: route.params.categoryName
            })}
          />
          <Stack.Screen 
            name="Game" 
            component={Game} 
            options={({ route }) => ({
            title: route.params.categoryName
            })}
          />
        </Stack.Navigator>
    );
  }
}

export default class App extends React.Component {
  render(){
    return (
      <>
        <BgAudio options={audio_options} />
        <NavigationContainer>
          <Tab.Navigator
            screenOptions={{
              headerStyle: {
                backgroundColor: '#90cbd3',
              },
              headerTintColor: '#fff',
              headerTitleStyle: {
                fontWeight: '900',
                fontSize: 30,
                fontFamily: 'UVNBanhMi',
              }
            }}
            tabBarOptions={{
              activeTintColor: 'blue',
              inactiveTintColor: '#fff',
              tabStyle: {
                backgroundColor: '#90cbd3',
              },
            }}
          >
            <Tab.Screen name="Home" component={Home} options={{ 
              headerShown: false, 
              tabBarIcon: ({ color, size }) => (
                <MaterialCommunityIcons name="home" color={color} size={size} />
              ),
            }}/>
            <Tab.Screen name="Shop" component={Shop} options={{ 
              tabBarIcon: ({ color, size }) => (
                <MaterialCommunityIcons name="storefront-outline" color={color} size={size} />
              ),
            }}/>
            <Tab.Screen name="Settings" component={Setting} options={{ 
              tabBarIcon: ({ color, size }) => (
                <MaterialCommunityIcons name="format-line-weight" color={color} size={size} />
              ),
            }}/>
          </Tab.Navigator>
        </NavigationContainer>
        <StatusBar style='dark' />
      </>
    );
  }
}

const styles = StyleSheet.create({
  container: {
    flex: 1,
    backgroundColor: '#fff',
    alignItems: 'stretch',
    justifyContent: 'center',
  }
});
const audio_options = {
  source:{local: require('./assets/musics/2.mp3')}
}
