import React from "react";
import { StyleSheet, Text, View } from 'react-native';


export default class Notification extends React.Component {

    constructor (props){
        super(props);
        this.state={
            _notification: "",
        }
    }

    componentDidMount(){
        const notification =  this.props.route.params.notification;
        if(notification){
            this.setState({_notification: "Hoan hô đúng rồi"})
        }
        else{
            this.setState({_notification: "Rất tiếc trả lời sai rồi"})
        }
    }

    render(){
        return (
            <View style={styles.container}>
                <View style={styles.body}>
                    <Text style={styles.not}>{this.state._notification}</Text>
                </View>
            </View>
        );
    }
}

const styles= StyleSheet.create({
    container: {
        flex: 1,
        backgroundColor: "#fff",
        alignItems: "center",
    },
    body: {
        backgroundColor: "red",
        flex: 1,
        width: "100%",
    },
    not: {

    },
});
