import React from "react";
import { StyleSheet, Text, TouchableOpacity, View } from 'react-native';


export default class Notification extends React.Component {

    constructor (props){
        super(props);
        this.state={
            _notification: "",
        }
    }

    componentDidMount(){
        const notification =  this.props.route.params.notification;
        if(notification){
            this.setState({_notification: "Hoan hô đúng rồi"})
        }
        else{
            this.setState({_notification: "Rất tiếc trả lời sai rồi"})
        }
    }

    render(){
        return (
            <View style={styles.container}>
                <View style={styles.body}>
                    <Text style={styles.not}>{this.state._notification}</Text>
                    <View style={styles.btns}>
                        <TouchableOpacity>
                            <Text>Làm lại</Text>
                        </TouchableOpacity>
                        <TouchableOpacity>
                            <Text>Giải thích</Text>
                        </TouchableOpacity>
                        <TouchableOpacity>
                            <Text>Câu tiếp</Text>
                        </TouchableOpacity>
                    </View>
                </View>
            </View>
        );
    }
}

const styles= StyleSheet.create({
    container: {
        flex: 1,
        backgroundColor: "#fff",
    },
    body: {
        backgroundColor: "red",
        flex: 1,
        width: "100%",
        alignItems: "center",
        justifyContent: "center",
    },
    not: {
        fontFamily: "UVNBanhMi",
        fontSize: 50,
        fontWeight: "bold",
        textAlign: "center",
        color: "#fff",
    },
    btns: {
        display: "flex",
        flexDirection: "row",
        width: "100%",
        margin: 20,
        justifyContent: "space-between",
    },
});
