    const {questions, onPress} = props;
    return (
        <TouchableOpacity onPress={onPress}>
            <View style={styles.container}>
                <Text style={styles.txt}>CÃ¢u {questions.id}</Text>
            </View>
        </TouchableOpacity>
    );
}

const styles = StyleSheet.create({
    container:{
        width: "80%",
        backgroundColor: '#fff',
