import React, {Component} from "react";
import { Text, View, Image, StyleSheet, FlatList, Dimensions } from "react-native";

export default class LearnScreen extends Component{
    constructor(props){
        super(props);

        this.state = {
            img: [
                {id: 1, url: "http://hiepnguyenhvu.tk/hiepnh/data/User/admin/home/AppGame/image/7.gif"},
                {id: 2, url: "http://hiepnguyenhvu.tk/hiepnh/data/User/admin/home/AppGame/image/7.gif"},
                {id: 3, url: "http://hiepnguyenhvu.tk/hiepnh/data/User/admin/home/AppGame/image/7.gif"},
                {id: 4, url: "http://hiepnguyenhvu.tk/hiepnh/data/User/admin/home/AppGame/image/7.gif"},
            ]
        }
    }
    
    render(){
        const { img }= this.state;
        return (
            <FlatList 
                data={img}
                pagingEnabled
                renderItem={({item})=>{
                    return(
                        <Image source={{uri: item.url}} style={styles.img}/>
                    )
                }}
                keyExtractor= {item=>item.id}
                contentContainerStyle={styles.container}
            />
        )
    }
}
const {height, width} = Dimensions.get('window');
const styles = StyleSheet.create({
    container: {
      flex: 1,
      alignItems: "center",
    },
    img: {
        flex: 1,
        width: width-40,
        height: height-200,
        marginVertical: 20,
        resizeMode: "cover"
    }
  });
