import React, {Component} from "react";
import { Text, BackgroundImage, StyleSheet, View } from "react-native";

export default class LearnScreen extends Component{
    render(){
        return (
            <BackgroundImage source={require('../assets/bg_learn.gif')} style={styles.container}>
                <View>
                    <Text>dfsdgdr</Text>
                </View>
            </BackgroundImage>
        )
    }
}

const styles = StyleSheet.create({
    container: {
      flex: 1,
      backgroundColor: '#fff',
      alignItems: 'stretch',
      justifyContent: 'center',
    },
    img: {
        
    }
  });
