import React from "react";
import axios from "axios";
import {Text, View, ImageBackground, TouchableOpacity, StyleSheet, Alert} from "react-native";

import { ScrollView } from "react-native-gesture-handler";

export default class Answer extends React.Component {
    constructor(props){
        super(props);
        this.state= {
          question:[]
        }
      }

    componentDidMount(){
        const database = this.props.route.params.data;
        const id = this.props.route.params.id;
        axios.get('http://8be3-113-175-74-249.ngrok.io/math?id=1')
          .then(res =>{
            this.setState({
              question: res.data
            })
          })
          .catch(error=>{
            console.error(error)
          })
      }
    
    check(answer){
        const { navigation } = this.props;
        if(answer==this.props.route.params.answer){
            navigation.navigate('Notification', {
                notification: true,
              });
        }
        else{
            navigation.navigate('Notification', {
                notification: false,
              });
        }
    }
    render(){
        const answer = this.props.route.params;
        console.log(this.state);
        return(
            <ImageBackground style={styles.container} source={{uri: answer.image}}>
                <View style={styles.info} >
                    {/* <Text style={styles.name}>{answer.questionName}</Text>
                    <View style={styles.answers}>
                        <View style={styles.answer}>
                            <TouchableOpacity style={styles.choice} onPress={()=>this.check(answer.a)}>
                                <Text style={styles.txt}>A. {answer.a}</Text>
                            </TouchableOpacity>
                            <TouchableOpacity style={styles.choice} onPress={()=>this.check(answer.b)}>
                                <Text style={styles.txt}>B. {answer.b}</Text>
                            </TouchableOpacity>
                        </View>
                        <View style={styles.answer}>
                            <TouchableOpacity style={styles.choice} onPress={()=>this.check(answer.c)}>
                                <Text style={styles.txt}>C. {answer.c}</Text>
                            </TouchableOpacity>
                            <TouchableOpacity style={styles.choice} onPress={()=>this.check(answer.d)}>
                                <Text style={styles.txt}>D. {answer.d}</Text>
                            </TouchableOpacity>
                        </View>
                    </View> */}
                    <Text style={styles.name}>{this.state.question["questionName"]}</Text>
                    <View style={styles.answers}>
                        <View style={styles.answer}>
                            <TouchableOpacity style={styles.choice} onPress={()=>this.check(answer.a)}>
                                <Text style={styles.txt}>A. {this.state.question["a"]}</Text>
                            </TouchableOpacity>
                            <TouchableOpacity style={styles.choice} onPress={()=>this.check(answer.b)}>
                                <Text style={styles.txt}>B. {this.state.question["b"]}</Text>
                            </TouchableOpacity>
                        </View>
                        <View style={styles.answer}>
                            <TouchableOpacity style={styles.choice} onPress={()=>this.check(answer.c)}>
                                <Text style={styles.txt}>C. {this.state.question["c"]}</Text>
                            </TouchableOpacity>
                            <TouchableOpacity style={styles.choice} onPress={()=>this.check(answer.d)}>
                                <Text style={styles.txt}>D. {this.state.question["d"]}</Text>
                            </TouchableOpacity>
                        </View>
                    </View>
                </View>
            </ImageBackground>
        );
    }
}

const styles = StyleSheet.create({
    container: {
        flex: 1,
        width: "100%",
        flexDirection: "column-reverse",
    },
    info: {
        padding: 8,
        alignItems: "center",
        marginBottom: 30,
    },
    name: {
        fontFamily: 'UVNBanhMi',
        width: "70%",
        fontSize: 24,
        marginBottom: 8,
        color: "#ff6000",
    },
    answers: {
        width: "70%",
    },
    choice: {
        backgroundColor: '#ffdf2b',
        alignItems: 'center',
        margin: 10,
        paddingHorizontal: 30,
        paddingVertical: 10,
        borderRadius: 10,
    },
    answer: {
        flexDirection: 'row',
        justifyContent: 'space-between',
    },
    txt: {
        fontSize: 24,
        fontFamily: 'UVNBanhMi',
        fontWeight: 'bold',
        color: '#ff6000',
    },
})
